package com.natrix.whatappclone.chat;import com.natrix.whatappclone.common.Response;import com.natrix.whatappclone.utility.Builder;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.security.core.Authentication;import org.springframework.web.bind.annotation.*;@RestController@RequiredArgsConstructor@RequestMapping("/api/v1/chats")public class ChatController {    private final ChatService chatService;    @GetMapping    public ResponseEntity<?> getChatsByReceiver(Authentication authentication){        return ResponseEntity.ok(this.chatService.getChatsByReceiverId(authentication));    }    @PostMapping    public ResponseEntity<?> createChat(@RequestParam(name = "sender-id") String senderId,                                               @RequestParam(name = "receiver-id") String receiverId){        final String chatId = this.chatService.createChat(senderId, receiverId);        Response response = Builder.of(Response::new)                                    .add(Response::setResponse, chatId)                                    .build();        return ResponseEntity.ok(response);    }}