package com.natrix.whatappclone.common;import jakarta.persistence.Column;import jakarta.persistence.MappedSuperclass;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import lombok.experimental.SuperBuilder;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.LastModifiedDate;import java.time.LocalDateTime;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@SuperBuilder@MappedSuperclasspublic class BaseAuditingEntity {    @CreatedDate    @Column(name = "created_date", nullable = false, updatable = false)    private LocalDateTime createdDate;    @LastModifiedDate    @Column(name = "updated_date", insertable = false)    private LocalDateTime updatedDate;}