package com.natrix.whatappclone.common;import com.natrix.whatappclone.utility.Builder;import lombok.Getter;import lombok.RequiredArgsConstructor;import lombok.Setter;@Getter@Setter@RequiredArgsConstructorpublic class Response {    private String response;    public static Response build(String responseMessage){        return Builder.of(Response::new)                       .add(Response::setResponse, responseMessage)                       .build();    }}