package com.natrix.whatappclone.message;import com.natrix.whatappclone.file.FileUtils;import com.natrix.whatappclone.utility.Builder;import org.springframework.stereotype.Service;@Servicepublic class MessageMapper {    public MessageResponse toMessageResponse(Message message) {        return Builder.of(MessageResponse::new)                       .add(MessageResponse::setId, message.getId())                       .add(MessageResponse::setCreatedAt, message.getCreatedDate())                       .add(MessageResponse::setContent, message.getContent())                       .add(MessageResponse::setMessageType, message.getMessageType())                       .add(MessageResponse::setMessageState, message.getMessageState())                       .add(MessageResponse::setSenderId, message.getSenderId())                       .add(MessageResponse::setReceiverId, message.getReceiverId())                       .add(MessageResponse::setMedia, FileUtils.readFileFromLocation(message.getMediaFilePath()))                       .build();    }}